INSERT INTO `category` (`id`, `name`, `image`, `link`, `date`, `description`, `active`, `meta_title`, `meta_description`) VALUES
(1, 'Pipet', 'bannerweb_T11_2020-10.jpg', 'pipet', '2020-11-26 17:00:00', '<p>\r\n	<span style=\"font-size:14px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-family: arial, helvetica, sans-serif; color: rgb(0, 0, 255);\"><em style=\"box-sizing: border-box; margin: 0px; padding: 0px; background-color: transparent; color: rgb(0, 0, 255); text-decoration-line: none;\"><strong style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-style: oblique;\">Dụng cụ th&iacute; nghiệm</strong></em>&nbsp;</span><span style=\"color: rgb(0, 0, 0); font-family: arial, helvetica, sans-serif;\">l&agrave; những vật dụng cần thiết li&ecirc;n quan đến độ an to&agrave;n v&agrave; t&iacute;nh ch&iacute;nh x&aacute;c trong c&aacute;c th&iacute; nghiệm, c&aacute;c kết quả nghi&ecirc;n cứu. Trong cuộc sống c&ocirc;ng nghệ như hiện nay, dụng cụ th&iacute; nghiệm ng&agrave;y c&agrave;ng được sử dụng nhiều tại c&aacute;c trường học, trung t&acirc;m th&iacute; nghiệm, nghi&ecirc;n cứu.&nbsp;</span></span></p>', 1, '121', 'ádasdasd'),
(3, 'đũa thủy tinh', 'bannerweb_T11_2020-11.jpg', 'dua-thuy-tinh', '2020-11-25 17:00:00', '<p>\r\n	<span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, sans-serif; font-size: 14px; text-align: justify;\">Nắp c&oacute; nhiều m&agrave;u gi&uacute;p dễ d&agrave;ng ph&acirc;n biệt.</span></p>', 1, 'giới thiệu cty LabVietchem', 'adasdas'),
(4, 'Bình thủy tinh', 'bannerweb_T11_2020-12.jpg', 'binh-thuy-tinh', '2020-11-25 17:00:00', '<p>\r\n	<span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, sans-serif; font-size: 14px; text-align: justify;\">Được d&ugrave;ng chủ yếu để bảo quản lạnh c&aacute;c mẫu như m&aacute;u to&agrave;n phần, huyết thanh v&agrave; tế b&agrave;o.</span></p>', 1, '', ''),
(7, 'ống đong', 'bannerweb_T11_2020-13.jpg', 'ong-dong', '2020-11-18 17:00:00', '<p>\r\n	adasd</p>', 1, '', ''),
(11, 'Phiến kính', 'bannerweb_T11_2020-14.jpg', 'phien-kinh', '2020-11-26 17:00:00', '<p>\r\n	abc</p>', 1, '', ''),
(12, 'Bình định mức', 'binh-dinh-muc.png', 'binh-dinh-muc', '2020-11-30 17:00:00', '<p>\r\n	<span style=\"color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; text-align: justify;\">B&igrave;nh định mức n&uacute;t nhựa class A 20ml, hiệu chuẩn ở 27&ordm;C, code 5646008, xuất xứ Borosil- Ấn Độ. Được l&agrave;m bằng thủy tinh borosilicate 3.3, USP loại I c&oacute; độ bền tốt v&agrave; hiệu chuẩn ở 27&ordm;C cho độ ch&iacute;nh x&aacute;c cao. Ứng dụng phổ biến trong c&aacute;c ph&ograve;ng th&iacute; nghiệm, để đo ch&iacute;nh x&aacute;c lượng chất lỏng đặc th&ugrave;, chuẩn bị hay lưu trữ c&aacute;c dung dịch chuẩn. Tu&acirc;n theo ti&ecirc;u chuẩn ISO / DIN 1042. Quy c&aacute;ch: 1c&aacute;i/hộp.</span></p>', 1, '', ''),
(14, 'cân điện tử', 'bannerweb_T11_2020-17.jpg', 'can-dien-tu', '2020-12-08 03:51:05', '<p>\r\n	&aacute;dasdsa</p>', 1, '', '');

