--

INSERT INTO `news` (`id`, `title`, `url`, `image`, `desc_en`, `description`, `new_id`, `comment_id`, `date`, `active`, `meta_title`, `meta_description`) VALUES
(2, 'Ăn dứa có tác dụng gì? Ăn nhiều có tốt không, bổ sung thế nào?', 'an-dua-co-tac-dung-gi', '120900547_1949843928546467_2092939014207747933_o.jpg', '<p>\r\n	<span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, sans-serif; font-size: 16px; text-align: justify;\">Dứa l&agrave; một loại tr&aacute;i c&acirc;y được nhiều người y&ecirc;u th&iacute;ch bởi những t&aacute;c dụng nổi bật m&agrave; ch&uacute;ng mang đến cho sức khỏe mỗi người. Vậy,&nbsp;</span><a href=\"https://house-home.vn/tin-tuc/tac-dung-cua-dua.html\" rel=\"noopener noreferrer\" style=\"font-family: arial, helvetica, sans-serif; font-size: 16px; text-align: justify; margin: 0px; padding: 0px; box-sizing: border-box; outline: none; text-decoration-line: none; color: rgb(0, 29, 255);\" target=\"_blank\" title=\"tác dụng của dứa\"><strong style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none;\">t&aacute;c dụng của dứa</strong>&nbsp;</a><span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, sans-serif; font-size: 16px; text-align: justify;\">l&agrave; g&igrave;, ăn nhiều c&oacute; tốt kh&ocirc;ng, bổ sung thế n&agrave;o?&nbsp;Tất cả sẽ được giải đ&aacute;p cụ thể trong b&agrave;i viết sau đ&acirc;y.&nbsp;</span></p>', '<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); font-family: Roboto, Helvetica, Arial, sans-senif; font-size: 16px; text-align: justify;\">\r\n	<span style=\"font-family: arial, helvetica, sans-serif; font-size: 16pt; color: rgb(62, 62, 62);\">1. Th&agrave;nh phần dinh dưỡng c&oacute; trong dứa&nbsp;</span></p>\r\n<h2 style=\"margin: 0.3em 0px 0em; padding: 0px; box-sizing: border-box; outline: none; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 20px; line-height: 24px; font-family: Roboto, Helvetica, Arial, sans-senif; color: rgb(62, 62, 62);\">\r\n	<img alt=\"Thành phần dinh dưỡng có trong dứa&nbsp;\" height=\"288\" src=\"https://sudospaces.com/house-home/2020/11/tac-dung-cua-dua-1.jpg\" style=\"margin: 0px auto; padding: 0px; box-sizing: border-box; outline: none; max-width: 100%; height: auto; display: block;\" width=\"512\" /></h2>\r\n<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); font-family: Roboto, Helvetica, Arial, sans-senif; font-size: 16px; text-align: center;\">\r\n	<em style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-size: 12pt;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif;\">Th&agrave;nh phần dinh dưỡng c&oacute; trong dứa&nbsp;</span></em></p>\r\n<h2 style=\"margin: 0.3em 0px 0em; padding: 0px; box-sizing: border-box; outline: none; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 20px; line-height: 24px; font-family: Roboto, Helvetica, Arial, sans-senif; color: rgb(62, 62, 62);\">\r\n	<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 16pt;\">2. T&aacute;c dụng của dứa đối với sức khỏe&nbsp;</span></h2>\r\n<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); font-family: Roboto, Helvetica, Arial, sans-senif; font-size: 16px; text-align: justify;\">\r\n	<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">Sau đ&acirc;y l&agrave; những c&ocirc;ng dụng của dứa đối với sức khỏe:&nbsp;</span></p>\r\n<div class=\"no_bullets contracted\" id=\"toc_container\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; color: rgb(62, 62, 62); font-family: Roboto, Helvetica, Arial, sans-senif; font-size: 16px;\">\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: justify;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-size: 12pt;\"><strong style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none;\"><span id=\"Kiem_soat_benh_viem_khop\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif;\">2.1 T&aacute;c dụng của quả dứa giảm đau xương khớp</span></strong></span></p>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51);\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">C&ocirc;ng dụng nổi bật của dứa kh&ocirc;ng thể kh&ocirc;ng nhắc đến ch&iacute;nh l&agrave; giảm t&igrave;nh trạng vi&ecirc;m khớp, giảm đau xương khớp hiệu quả. Bởi trong quả dứa c&oacute; chứa c&aacute;c loại&nbsp;enzyme proteolytic c&oacute; t&ecirc;n l&agrave; bromelain, c&oacute; t&aacute;c dụng gi&uacute;p ph&aacute; vỡ protein phức tạp đồng thời chống vi&ecirc;m hiệu quả.&nbsp;</span></p>\r\n	<figure class=\"sudo-media-item\" style=\"margin: 0px; padding: 0px 0px 10px; box-sizing: border-box; outline: none;\">\r\n		<img alt=\"Tác dụng của quả dứa giảm đau xương khớp\" src=\"https://sudospaces.com/house-home/2020/11/tac-dung-cua-dua-2.jpg\" style=\"margin: 0px auto; padding: 0px; box-sizing: border-box; outline: none; max-width: 100%; height: auto; display: block;\" width=\"750\" /></figure>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: center;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-size: 12pt;\"><em style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif;\">T&aacute;c dụng của quả dứa giảm đau xương khớp</span></em></span></p>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: justify;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-size: 12pt;\"><strong style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none;\"><span id=\"Tang_cuong_he_mien_dich\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif;\">2.2 C&ocirc;ng dụng của dứa gi&uacute;p tăng cường hệ miễn dịch</span></strong></span></p>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51);\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">Mỗi ng&agrave;y ăn dứa với lượng vừa đủ gi&uacute;p bạn nạp th&ecirc;m hơn&nbsp;130% vitamin C cần thiết cho cơ thể, để gi&uacute;p tăng cường hệ miễn dịch, giảm&nbsp;bệnh tật.</span></p>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51);\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">Bởi khi đi v&agrave;o cơ thể, vitamin C gi&uacute;p k&iacute;ch th&iacute;ch hoạt động của bạch cầu v&agrave; như một chất chống oxy h&oacute;a để bảo vệ cơ thể khỏi những t&aacute;c động xấu của c&aacute;c gốc tự do.</span></p>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: justify;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-size: 12pt;\"><strong style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none;\"><span id=\"Trai_dua_tot_cho_te_bao_va_mo\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif;\">2.3 Lợi &iacute;ch của dứa tốt cho tế b&agrave;o v&agrave; m&ocirc;</span></strong></span></p>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51);\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; text-align: justify; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">Vitamin C c&oacute; trong dứa c&ograve;n đ&oacute;ng một vai tr&ograve; v&ocirc; c&ugrave;ng quan trọng để tạo collagen cho cơ thể. Ch&uacute;ng l&agrave; th&agrave;nh phần thiết yếu tạo n&ecirc;n th&agrave;nh mạch của da, m&aacute;u, xương c&ugrave;ng c&aacute;c cơ quan kh&aacute;c.</span></p>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51);\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; text-align: justify; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">B&ecirc;n cạnh đ&oacute;, lượng vitamin C c&oacute; trong tr&aacute;i dứa gi&uacute;p chữa l&agrave;nh tổn thương rất hiệu quả, để gi&uacute;p bạn ph&ograve;ng tr&aacute;nh bệnh tật v&agrave; nhiễm tr&ugrave;ng hiệu quả.&nbsp;</span></p>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: justify;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-size: 12pt;\"><strong style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none;\"><span id=\"An_dua_co_tac_dung_gi_voi_he_tieu_hoa\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif;\">2.4 T&aacute;c dụng với hệ ti&ecirc;u h&oacute;a</span></strong></span></p>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: justify;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">Dứa l&agrave; nguồn cung cấp lượng chất xơ h&ograve;a tan v&agrave; kh&ocirc;ng h&ograve;a tan voo c&ugrave;ng phong ph&uacute;. Khi ăn dứa đều đặn sẽ gi&uacute;p bạn tr&aacute;nh khỏi t&igrave;nh trạng bị ti&ecirc;u chảy, t&aacute;o b&oacute;n, ph&ograve;ng ngừa hội chứng đ&ocirc;ng m&aacute;u v&agrave; những hội chứng ruột k&iacute;ch th&iacute;ch.</span></p>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: justify;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">Chất xơ trong tr&aacute;i dứa gi&uacute;p tăng lượng ph&acirc;n, th&uacute;c đẩy qu&aacute; tr&igrave;nh thức ăn qua đường ti&ecirc;u h&oacute;a một c&aacute;ch b&igrave;nh thường, quan trọng hơn cả gi&uacute;p k&iacute;ch th&iacute;ch giải ph&oacute;ng chất ti&ecirc;u h&oacute;a để gi&uacute;p h&ograve;a tan thực phẩm.</span></p>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: justify;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-size: 12pt;\"><strong style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none;\"><span id=\"Dua_rat_tot_cho_suc_khoe_cua_mat\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif;\">2.5 Tốt cho sức khỏe của mắt</span></strong></span></p>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: justify;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">Chất beta - carotene c&oacute; trong tr&aacute;i dứa c&oacute; t&aacute;c dụng gi&uacute;p tr&igrave; ho&atilde;n t&igrave;nh trạng tho&aacute;i h&oacute;a bạch cầu, đ&acirc;y l&agrave; một trong những căn bệnh g&acirc;y ảnh hưởng tới thị lực của người cao tuổi.</span></p>\r\n	<figure class=\"sudo-media-item\" style=\"margin: 0px; padding: 0px 0px 10px; box-sizing: border-box; outline: none;\">\r\n		<img alt=\"Tốt cho sức khỏe của mắt\" src=\"https://sudospaces.com/house-home/2020/11/tac-dung-cua-dua-3.jpg\" style=\"margin: 0px auto; padding: 0px; box-sizing: border-box; outline: none; max-width: 100%; height: auto; display: block;\" width=\"750\" /></figure>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: center;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-size: 12pt;\"><em style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif;\">Tốt cho sức khỏe của mắt</span></em></span></p>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: justify;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-size: 12pt;\"><strong style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none;\"><span id=\"An_dua_tot_cho_he_tuan_hoan_mau\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif;\">2.6 Tốt cho hệ tuần ho&agrave;n m&aacute;u</span></strong></span></p>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: justify;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">Dứa l&agrave; một tr&aacute;i c&acirc;y&nbsp;cung cấp một lượng đồng kh&aacute; lớn cho cơ thể, đ&acirc;y l&agrave; loại kho&aacute;ng chất c&oacute; vai tr&ograve; rất quan trọng trong phản ứng enzyme v&agrave; trong c&aacute;c hợp chất kh&aacute;c của cơ thể. Đặc biệt, đồng c&ograve;n l&agrave; yếu tố thiết yếu trong việc h&igrave;nh th&agrave;nh c&aacute;c tế b&agrave;o hồng cầu khỏe mạnh, để tăng lượng oxy cho c&aacute;c cơ quan kh&aacute;c nhau gi&uacute;p ch&uacute;ng hoạt động tốt hơn.&nbsp;Ngo&agrave;i ra, đồng c&ograve;n c&oacute; t&aacute;c dụng tăng nhận thức v&agrave; rất tốt cho hệ thần kinh, để ph&ograve;ng tr&aacute;nh t&igrave;nh trạng rối loạn thần kinh, bệnh Alzheimer hay chứng mất tr&iacute; nhớ ở người cao tuổi.</span></p>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: justify;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-size: 12pt;\"><strong style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif;\">2.7 T<span data-mention=\"tác dụng của dứa với bà bầu\" id=\"input_part_0\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none;\">&aacute;c dụng của dứa với b&agrave; bầu</span></span></strong></span></p>\r\n	<div id=\"input_line_18\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">B&agrave; bầu c&oacute; n&ecirc;n ăn dứa hay kh&ocirc;ng, bởi nhiều người lo lắng rằng ăn dứa c&oacute; thể&nbsp;dẫn tới sảy thai do chất Bromelain trong dứa l&agrave;m mềm tử cung, thế nhưng chưa một nghi&ecirc;n cứu n&agrave;o l&agrave;m r&otilde; hay chứng minh cụ thể. M&agrave; ngược lại, sau 3 th&aacute;ng đầu ti&ecirc;n của thai kỳ, mẹ bầu ăn dứa sẽ c&oacute; những t&aacute;c dụng như sau:</span></div>\r\n	<ul style=\"margin: 0.5em 0px 0.5em 10px; padding-right: 0px; padding-left: 0px; box-sizing: border-box; outline: none; text-align: justify;\">\r\n		<li style=\"margin: 0.5em 0px 0.5em 30px; padding: 0px; box-sizing: border-box; outline: none; list-style-type: disc; line-height: 1.4em; color: rgb(51, 51, 51);\">\r\n			<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">Hỗ trợ hệ miễn dịch cho mẹ bầu: Lượng vitamin C trong dứa gi&uacute;p tăng cường, bảo vệ hệ miễn dịch rất tốt cho mẹ bầu. B&ecirc;n cạnh đ&oacute;, chất bromelain cũng gi&uacute;p ph&ograve;ng tr&aacute;nh c&aacute;c dấu hiệu cảm lạnh th&ocirc;ng thương, do đ&oacute;, nếu mẹ bầu đang bị đau họng hoặc cảm lạnh th&igrave; h&atilde;y thử một miếng dứa.&nbsp;</span></li>\r\n		<li style=\"margin: 0.5em 0px 0.5em 30px; padding: 0px; box-sizing: border-box; outline: none; list-style-type: disc; line-height: 1.4em; color: rgb(51, 51, 51);\">\r\n			<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">Dứa gi&uacute;p ph&ograve;ng tr&aacute;nh t&aacute;o b&oacute;n: Lượng chất xơ c&oacute; trong dứa gi&uacute;p b&agrave; bầu ph&ograve;ng tr&aacute;nh t&igrave;nh trạng t&aacute;o b&oacute;n khi mang bầu, gi&uacute;p qu&aacute; tr&igrave;nh ti&ecirc;u h&oacute;a diễn ra nhanh hơn.</span></li>\r\n		<li style=\"margin: 0.5em 0px 0.5em 30px; padding: 0px; box-sizing: border-box; outline: none; list-style-type: disc; line-height: 1.4em; color: rgb(51, 51, 51);\">\r\n			<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">Giảm t&igrave;nh trạng ốm ngh&eacute;n: Ở một số trường hợp, dứa c&oacute; t&aacute;c dụng gi&uacute;p mẹ bầu giảm t&igrave;nh trạng ốm ngh&eacute;n.</span></li>\r\n	</ul>\r\n	<figure class=\"sudo-media-item\" style=\"margin: 0px; padding: 0px 0px 10px; box-sizing: border-box; outline: none;\">\r\n		<img alt=\"Tác dụng của dứa với bà bầu\" src=\"https://sudospaces.com/house-home/2020/11/tac-dung-cua-dua-4.jpg\" style=\"margin: 0px auto; padding: 0px; box-sizing: border-box; outline: none; max-width: 100%; height: auto; display: block;\" width=\"750\" /></figure>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: center;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-size: 12pt;\"><em style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif;\">T<span data-mention=\"tác dụng của dứa với bà bầu\" id=\"input_part_0\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none;\">&aacute;c dụng của dứa với b&agrave; bầu</span></span></em></span></p>\r\n	<h2 style=\"margin: 0.3em 0px 0em; padding: 0px; box-sizing: border-box; outline: none; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 20px; line-height: 24px; text-align: justify;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 16pt;\">3. C&oacute; n&ecirc;n ăn dứa nhiều kh&ocirc;ng? Lưu &yacute; khi sử dụng</span></h2>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: justify;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">Việc bổ sung qu&aacute; nhiều&nbsp;dứa thực chất kh&ocirc;ng tốt v&agrave; ảnh hưởng xấu đến cơ thể như sau:&nbsp;</span></p>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: justify;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-size: 12pt;\"><strong style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif;\">3.1 G&acirc;y ra c&aacute;c phản ứng dị ứng</span></strong></span></p>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: justify;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">Dứa c&oacute; chứa bromelain, c&oacute; khả năng ti&ecirc;u h&oacute;a protein. Khi ăn dứa, một số người sẽ bị phản ứng dị ứng nhẹ như ngứa, vi&ecirc;m da mặt v&agrave; lưỡi. Th&ocirc;ng thường, những phản ứng n&agrave;y sẽ tự giảm sau v&agrave;i giờ, nhưng nếu kh&ocirc;ng thuy&ecirc;n giảm, cần hỏi &yacute; kiến ​​b&aacute;c sĩ ngay.</span></p>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: justify;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">Một số người cũng c&oacute; thể ph&aacute;t triển bệnh hen suyễn v&agrave; vi&ecirc;m mũi khi họ ăn dứa trong một thời gian d&agrave;i.</span></p>\r\n	<p class=\"ftwp-heading\" id=\"ftoc-heading-2\" style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: justify;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-size: 12pt;\"><strong style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif;\">3.2 Phản ứng với thuốc trong cơ thể&nbsp;</span></strong></span></p>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: justify;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">Bromelain c&oacute; trong dứa tương t&aacute;c với một số loại thuốc trong cơ thể m&agrave; bạn đang uống, do đ&oacute; bạn cần phải cẩn thận khi ăn dứa nếu đang d&ugrave;ng thuốc.</span></p>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: justify;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\"><strong style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none;\">V&iacute; dụ:&nbsp;</strong></span></p>\r\n	<ul style=\"margin: 0.5em 0px 0.5em 10px; padding-right: 0px; padding-left: 0px; box-sizing: border-box; outline: none;\">\r\n		<li style=\"margin: 0.5em 0px 0.5em 30px; padding: 0px; box-sizing: border-box; outline: none; list-style-type: disc; line-height: 1.4em; color: rgb(51, 51, 51); text-align: justify;\">\r\n			<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">Chất trong dứa l&agrave;m tăng sự hấp thu một số loại kh&aacute;ng sinh: amoxicillin v&agrave; tetracycline.</span></li>\r\n		<li style=\"margin: 0.5em 0px 0.5em 30px; padding: 0px; box-sizing: border-box; outline: none; list-style-type: disc; line-height: 1.4em; color: rgb(51, 51, 51); text-align: justify;\">\r\n			<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">Tăng nguy cơ chảy m&aacute;u nếu bạn đang sử dụng thuốc l&agrave;m lo&atilde;ng m&aacute;u như warfarin, clopidogrel v&agrave; aspirin.</span></li>\r\n		<li style=\"margin: 0.5em 0px 0.5em 30px; padding: 0px; box-sizing: border-box; outline: none; list-style-type: disc; line-height: 1.4em; color: rgb(51, 51, 51); text-align: justify;\">\r\n			<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">Nếu bạn d&ugrave;ng thuốc an thần như thuốc chống động kinh, thuốc an thần, thuốc benzodiazepin, rượu, thuốc chống trầm cảm ba v&ograve;ng v&agrave; thuốc điều trị chứng mất ngủ.. cần hết sức lưu &yacute; khi ăn dứa.&nbsp;</span></li>\r\n	</ul>\r\n	<p class=\"ftwp-heading\" id=\"ftoc-heading-3\" style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: justify;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-size: 12pt;\"><strong style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif;\">3.3 G&acirc;y nhạy cảm với răng</span></strong></span></p>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: justify;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">Độ pH của dứa dao động từ 3,3 đến 5,2, c&oacute; nghĩa l&agrave; dứa c&oacute; t&iacute;nh axit. Nếu bạn thường xuy&ecirc;n ăn dứa trong một thời gian d&agrave;i, men răng của bạn sẽ bị b&agrave;o m&ograve;n, dẫn đến răng nhạy cảm.</span></p>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: justify;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">Bạn cũng n&ecirc;n tr&aacute;nh uống nước dứa chưa pha lo&atilde;ng hoặc cắn dứa nếu như răng bạn vốn dĩ nhạy cảm.</span></p>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: justify;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">Thận trọng khi ăn dứa để ngăn chặn t&igrave;nh trạng răng của bạn trở n&ecirc;n tồi tệ hơn.</span></p>\r\n	<figure class=\"sudo-media-item\" style=\"margin: 0px; padding: 0px 0px 10px; box-sizing: border-box; outline: none;\">\r\n		<img alt=\"Gây nhạy cảm với răng\" src=\"https://sudospaces.com/house-home/2020/11/tac-dung-cua-dua-5.jpg\" style=\"margin: 0px auto; padding: 0px; box-sizing: border-box; outline: none; max-width: 100%; height: auto; display: block;\" width=\"750\" /></figure>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: center;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-size: 12pt;\"><em style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif;\">G&acirc;y nhạy cảm với răng</span></em></span></p>\r\n	<p class=\"ftwp-heading\" id=\"ftoc-heading-4\" style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: justify;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\"><strong style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none;\">3.4 Tăng lượng đường trong m&aacute;u</strong></span></p>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51);\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; text-align: justify;\">D</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; text-align: justify;\">ứa c&oacute; chứa đường fructose, đ&acirc;y l&agrave; một loại đường l&agrave;m tăng lượng glucose trong m&aacute;u. V&igrave; vậy, nếu c&oacute; bất cứ dấu hiệu n&agrave;o, h&atilde;y kh&aacute;m b&aacute;c sỹ để biết r&otilde; t&igrave;nh trạng sức khỏe v&agrave; c&acirc;n nhắc n&ecirc;n ăn hay kh&ocirc;ng.&nbsp;</span></span></p>\r\n	<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\"><strong style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none;\">3.5 G&acirc;y ti&ecirc;u chảy</strong></span></div>\r\n<div class=\"no_bullets contracted\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; color: rgb(62, 62, 62); font-family: Roboto, Helvetica, Arial, sans-senif; font-size: 16px;\">\r\n	&nbsp;</div>\r\n<div class=\"no_bullets contracted\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; color: rgb(62, 62, 62); font-family: Roboto, Helvetica, Arial, sans-senif; font-size: 16px;\">\r\n	<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">Dứa c&oacute; chứa nhiều chất xơ, gi&uacute;p hỗ trợ ti&ecirc;u h&oacute;a, nhưng nếu dư thừa quả dứa sẽ dẫn đến c&aacute;c vấn đề như n&ocirc;n v&agrave;&nbsp;ti&ecirc;u chảy.</span>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: justify;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">H&atilde;y uống nhiều nước nếu bạn bị c&aacute;c vấn đề như vậy đồng thời đến gặp b&aacute;c sĩ nếu t&igrave;nh trạng của bạn kh&ocirc;ng cải thiện trong v&agrave;i giờ hoặc nếu t&igrave;nh trạng đ&oacute; trở n&ecirc;n tồi tệ hơn.</span></p>\r\n	<figure class=\"sudo-media-item\" style=\"margin: 0px; padding: 0px 0px 10px; box-sizing: border-box; outline: none;\">\r\n		<img alt=\"Gây tiêu chảy\" src=\"https://sudospaces.com/house-home/2020/11/tac-dung-cua-dua-6.jpg\" style=\"margin: 0px auto; padding: 0px; box-sizing: border-box; outline: none; max-width: 100%; height: auto; display: block;\" width=\"750\" /></figure>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: center;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-size: 12pt;\"><em style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif;\">G&acirc;y ti&ecirc;u chảy</span></em></span></p>\r\n	<p class=\"ftwp-heading\" id=\"ftoc-heading-6\" style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: justify;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\"><strong style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none;\">3.6 Kh&ocirc;ng n&ecirc;n ăn dứa xanh</strong></span></p>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51); text-align: justify;\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">Ăn một quả dứa chưa ch&iacute;n hoặc uống nước &eacute;p dứa xanh cực kỳ nguy hiểm v&igrave; c&oacute; thể dẫn đến ti&ecirc;u chảy nghi&ecirc;m trọng v&agrave; n&ocirc;n, rất kh&oacute; điều trị.&nbsp;</span></p>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51);\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">Với những chia sẻ qua b&agrave;i viết n&agrave;y đ&atilde; gi&uacute;p bạn c&oacute; thể hiểu hơn về những&nbsp;<strong style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none;\">t&aacute;c dụng của dứa</strong>&nbsp;mang đến cho cơ thể, lưu &yacute; để&nbsp;bổ sung sao cho hiệu quả v&agrave; an to&agrave;n nhất. Ch&uacute;c bạn nhiều sức khỏe!&nbsp;&nbsp;</span></p>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51);\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\"><strong style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none;\">=&gt;&gt; XEM TH&Ecirc;M:&nbsp;</strong></span></p>\r\n	<div class=\"name\" style=\"margin: 0px; padding: 0px 0px 10px; box-sizing: border-box; outline: none; color: rgb(0, 174, 239); font-size: 20px;\">\r\n		<h1 style=\"margin: 0.3em 0px 0em; padding: 0px; box-sizing: border-box; outline: none; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 24px; line-height: 28px; text-align: justify;\">\r\n			<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-size: 12pt; color: rgb(0, 0, 255);\"><a href=\"https://house-home.vn/tin-tuc/tac-dung-cua-muop-dang.html\" rel=\"noopener noreferrer\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; text-decoration-line: none; color: rgb(0, 0, 255);\" target=\"_blank\" title=\"Mướp đắng có tác dụng gì? Những lưu ý cần ghi nhớ khi sử dụng\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif;\">Mướp đắng c&oacute; t&aacute;c dụng g&igrave;? Những lưu &yacute; cần ghi nhớ khi sử dụng</span></a></span></h1>\r\n	</div>\r\n	<p style=\"margin: 0px; padding: 10px 0px 0px; box-sizing: border-box; outline: none; line-height: 2em; color: rgb(51, 51, 51);\">\r\n		<span style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\"><strong style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none;\">T&igrave;m kiếm li&ecirc;n quan:&nbsp;</strong></span></p>\r\n	<ul style=\"margin: 0.5em 0px 0.5em 10px; padding-right: 0px; padding-left: 0px; box-sizing: border-box; outline: none;\">\r\n		<li id=\"input_line_1\" style=\"margin: 0.5em 0px 0.5em 30px; padding: 0px; box-sizing: border-box; outline: none; list-style-type: disc; line-height: 1.4em; color: rgb(51, 51, 51);\">\r\n			<span data-mention=\"tác dụng của quả dứa dại\" id=\"input_part_0\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; outline: none; font-family: arial, helvetica, sans-serif; font-size: 12pt;\">t&aacute;c dụng của quả dứa dại</span></li>\r\n		<li id=\"input_line_3\" style=\"margin: 0.5em 