INSERT INTO `product` (`id`, `masp`, `name`, `url`, `image`, `price`, `description_en`, `description`, `cate_id`, `brand_id`, `date`, `active`, `view`, `meta_title`, `meta_description`) VALUES
(22, 'sp-01', '(Amber, 2 vạch chia) 100ml 6 chiếc/gói Finetech', 'amber-finetech', 'ong-so-mau.jpg', 360000, '<div class=\"description\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">\r\n	<div class=\"description\" style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">\r\n		<p style=\"box-sizing: border-box; margin: 0px; padding: 0px; text-align: justify;\">\r\n			Nồi hấp tiệt tr&ugrave;ng LHA-F10 l&agrave; một nồi hấp đơn ch&acirc;n kh&ocirc;ng với thể t&iacute;ch 360 L. Nhiệt độ l&agrave;m việc tối đa 134&deg;C, n&oacute; khử tr&ugrave;ng hiệu quả chất lỏng v&agrave; chất thải cần loại bỏ. C&aacute;ch nhiệt chất lượng cao v&agrave; cấu tr&uacute;c k&iacute;n l&agrave;m cho n&oacute; hiệu quả để khử tr&ugrave;ng quy m&ocirc; lớn. Được trang bị với c&aacute;c t&iacute;nh năng an to&agrave;n như tự động tắt ngăn chặn qu&aacute; tải hiện tại, bảo vệ tự động chống lại nhiệt độ tăng l&ecirc;n.&nbsp;M&agrave;n h&igrave;nh LCD kỹ thuật số để theo d&otilde;i trạng th&aacute;i l&agrave;m việc v&agrave; c&aacute;c th&ocirc;ng số</p>\r\n	</div>\r\n	<div class=\"fb-share-button fb_iframe_widget\" data-href=\"https://labvietchem.com.vn/noi-hap-tiet-trung-nam-ngang-lha-f11-labtron.html\" data-layout=\"button_count\" fb-iframe-plugin-query=\"app_id=&amp;container_width=442&amp;href=https%3A%2F%2Flabvietchem.com.vn%2Fnoi-hap-tiet-trung-nam-ngang-lha-f11-labtron.html&amp;layout=button_count&amp;locale=en_US&amp;sdk=joey\" fb-xfbml-state=\"rendered\" style=\"box-sizing: border-box; margin: 10px 0px 0px; padding: 0px; display: inline-block; position: relative; font-size: 13px;\">\r\n		&nbsp;</div>\r\n</div>\r\n<p>\r\n	&nbsp;</p>', '<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; line-height: 20px; text-align: justify; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 13px;\">\r\n	<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-size: 14px; line-height: 30px; font-family: arial, helvetica, sans-serif;\">- Lọc kh&iacute;, h&oacute;a chất mạnh v&agrave; axit mạnh. C&oacute; th&ecirc;m lớp tiền lọc gi&uacute;p tối ưu h&oacute;a hiệu suất lọc c&oacute; thể lọc dung dịch c&oacute; độ nhớt cao hơn</span></p>\r\n<p class=\"font_7\" style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; line-height: 20px; text-align: justify; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 13px;\">\r\n	<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-size: 14px; line-height: 30px; font-family: arial, helvetica, sans-serif;\">- Ứng dụng trong qu&aacute; tr&igrave;nh ph&acirc;n t&iacute;ch mẫu tr&ecirc;n c&aacute;c hệ m&aacute;y đ&ograve;i hỏi độ tinh khiết cao như: m&aacute;y HPLC v&agrave; LC-MS. Sử dụng phổ biến trong c&aacute;c ph&ograve;ng th&iacute; nghiệm, viện nghi&ecirc;n cứu h&oacute;a sinh, y học....</span></p>', 4, 6, '2020-11-30 17:00:00', 1, NULL, 'sp-01', 'sp-01'),
(23, 'sp-02', 'Syringe lọc RC 13mm x 0.22um Finetech', 'ádas', 'sp-02.png', 25000, '<p>\r\n	<span style=\"color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; text-align: justify;\">Syringe lọc t&aacute;i sinh/ RC 13mm x 0.22um Finetech, code RC013N022I, sản xuất bởi h&atilde;ng Finetech - Đ&agrave;i Loan. Chất liệu b&ecirc;n ngo&agrave;i bằng PTFE c&oacute; độ ổn định nhiệt tốt, k&iacute;ch thước lỗ 0.22um, m&agrave;ng lọc loại RC bằng Nylon th&iacute;ch hợp để lọc sạch c&aacute;c dung dịch hữu cơ, dung m&ocirc;i mạnh... Ứng dụng trong qu&aacute; tr&igrave;nh ph&acirc;n t&iacute;ch mẫu tr&ecirc;n c&aacute;c hệ m&aacute;y đ&ograve;i hỏi độ tinh khiết cao như: m&aacute;y HPLC v&agrave; LC-MS, AAS,... Sử dụng phổ biến trong c&aacute;c ph&ograve;ng th&iacute; nghiệm, viện nghi&ecirc;n cứu h&oacute;a sinh, y học..... Quy c&aacute;ch: 100 chiếc/ g&oacute;i.</span></p>', '<table style=\"margin-top: 20px; margin-right: 0px; margin-bottom: 20px; padding: 0px; border-collapse: collapse; border-spacing: 0px; border-style: solid; border-color: rgb(204, 204, 204); width: 735px; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 13px; height: 444px; margin-left: 0px !important;\">\r\n	<tbody style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">\r\n		<tr style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">\r\n			<td style=\"box-sizing: border-box; margin: 0px; padding: 8px; border-style: solid; border-color: rgb(204, 204, 204); line-height: 30px; background: rgb(243, 244, 246); width: 188px;\">\r\n				<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-size: 14px; line-height: 30px; font-family: arial, helvetica, sans-serif;\"><strong style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">T&ecirc;n sản phẩm:</strong></span></td>\r\n			<td style=\"box-sizing: border-box; margin: 0px; padding: 8px; border-style: solid; border-col